<template>
  <a-layout id="components-layout-demo-custom-trigger">
    <a-layout-sider
      :trigger="null"
      collapsible
      v-model="collapsed"
    >
      <div class="logo">
        <a href="https://github.com/qiugu/vue-qiugu-ms">
          <img height="30" src="data:image/svg+xml;base64," alt="logo">
          <img height="15" v-if="!collapsed" src="data:image/svg+xml;base64," alt="logo">
        </a>
      </div>
      <a-menu theme="dark" mode="inline" v-model="keyArr"  @click="goPage">
        <a-menu-item key="about">
          <a-icon type="user" />
          <span>nav 1</span>
        </a-menu-item>
        <a-menu-item key="home">
          <a-icon type="video-camera" />
          <span>nav 2</span>
        </a-menu-item>
        <a-menu-item key="pattern">
          <a-icon type="upload" />
          <span>nav 3</span>
        </a-menu-item>
      </a-menu>
    </a-layout-sider>
    <a-layout>
      <a-layout-header style="background: #fff; padding: 0">
        <a-icon
          class="trigger"
          :type="collapsed ? 'menu-unfold' : 'menu-fold'"
          @click="()=> collapsed = !collapsed"
        />
      </a-layout-header>
      <a-layout-content :style="{ margin: '24px 16px', padding: '24px', background: '#fff', minHeight: '280px' }">
        <router-view/>
      </a-layout-content>
    </a-layout>
  </a-layout>
</template>
<script>
export default {
  data(){
    return {
      collapsed: false,
      keyArr: ['about']
    }
  },
  methods: {
    goPage(item) {
      this.keyArr = [item.key]
      this.$router.push({name: item.key})
    }
  },
  mounted() {
     this.keyArr = [this.$router.currentRoute.name]
  }
}
</script>
